'use strict';

var _adView = require('ad-view');

var _adControl = require('ad-control');

var _adEvents = require('ad-events');

var _Common = require('../common/js/control/Common.js');

require('./images/template_image.png');

var _adUi = require('ad-ui');

/* -- CONTROL ----------------------------------------------------------------------------------------------------
 *
 *
 *
 */
/* BUILD SOURCE: Standard - Base / OPTIONS: Sample UIComponents / AdApp: 3.0.3 / AdHtml: v0.1.3 / Created: 06/20/18 11:35am */
window.Control = new function () {
	this.prepare = function (fbaContent) {
		var _this = this;

		console.log('Control.prepare()');
		_adControl.Core.init(fbaContent).then(function () {
			return _Common.Common.init();
		}).then(function () {
			return _adControl.Core.loadDynamic();
		}).then(function () {
			_this.prepareBuild();
		}).catch(function (err) {
			throw err;
		});
	};

	// build runtime begins here ----------->
	this.prepareBuild = function () {
		console.log('Control.prepareBuild()');
		Control.preMarkup();

		View.main = new Main();
		View.mainBorder = new MainBorder();

		Control.postMarkup();

		Animation.startAd();
	};

	this.preMarkup = function () {
		console.log('Control.preMarkup()');
	};

	this.postMarkup = function () {
		console.log('Control.postMarkup()');
		// listen for default exit
		_adEvents.Gesture.add(View.main, _adEvents.GestureEvent.CLICK, Control.handleClick);
	};

	// IMPORTANT!!! If this method has content, Call this function when your animation is complete!
	this.animationComplete = function () {
		console.log('Control.animationComplete()');
	};

	this.handleClick = function (event) {
		Network.exit(clickTag);
	};
}();

/* -- VIEW ------------------------------------------------------------------------------------------------------
 *
 *
 *
 */

// ==============================================================================================================
function Main() {
	var T = _adView.Markup.get('main');
	_adView.Styles.setCss(T, {
		position: 'absolute',
		width: adParams.adWidth,
		height: adParams.adHeight,
		opacity: 0,
		left: '0px',
		top: '0px',
		overflow: 'hidden',
		userSelect: 'none'
	});

	T.logoContainer = new _adUi.UIImage({
		id: 'logo-container',
		target: T,
		source: 'template_image',
		css: {
			x: 9,
			y: 60
		}
	});

	T.txtGreeting = new _adUi.UITextField({
		id: 'txt-greeting',
		target: T,
		css: {
			width: 200,
			height: 30,
			color: '#ff1414'
		},
		align: _adView.Align.CENTER,
		fontSize: 40,
		fontFamily: 'template_font',
		format: _adUi.TextFormat.INLINE_FIT,
		alignText: _adView.Align.CENTER,
		bufferText: {
			left: 5,
			right: 5
		},
		leading: 1,
		text: 'MOBILE ADS'
	});

	_adView.Effects.textDropShadow({
		target: T.txtGreeting,
		angle: 45,
		distance: 2,
		size: 2,
		color: '#000000',
		alpha: 0.5
	});

	T.buttonCta = new _adUi.UIButton({
		id: 'btn-cta',
		target: T,
		css: {
			width: 150,
			height: 40,
			backgroundColor: 'grey',
			borderRadius: 10
		},
		align: {
			x: _adView.Align.CENTER,
			y: {
				type: _adView.Align.BOTTOM,
				offset: -30
			}
		},
		icon: [new _adUi.UITextField({
			css: {
				width: 150,
				height: 40,
				color: '#ffffff'
			},
			fontSize: 12,
			fontFamily: 'template_font',
			format: _adUi.TextFormat.INLINE_FIT,
			alignText: _adView.Align.CENTER,
			text: 'CLICK FOR MORE'
		})]
	});

	// add background color to ad if needed
	_adView.Styles.setCss(T, { 'background-color': '#cccccc' });

	return T;
}

// ==============================================================================================================
function MainBorder() {
	new _adUi.UIBorder({
		target: View.main,
		size: 1,
		color: '#000000'
	});
}

/* -- ANIMATION -------------------------------------------------------------------------------------------------
 *
 *
 *
 */
window.Animation = new function () {
	this.startAd = function () {
		console.log('Animation.startAd()');

		// show the main container
		global.removePreloader();
		_adView.Styles.setCss(View.main, { opacity: 1 });

		TweenLite.from(View.main.logoContainer, 1, { y: -40 });
		TweenLite.from(View.main.txtGreeting, 1, { y: 330 });
	};
}();

